es waiver', '[\"Bachelors\"]', '[\"Bachelors (All)\"]', '\"\\\"Undergraduates\\\"\"', NULL, NULL, 0.00, 0.00, 0.00, 0.00, '[\"\"]', '[\"\"]', '[\"\"]', NULL, '[\"\"]', NULL, '[\"\"]', 0, 0, '[\"\"]', 600000.00, '', '[\"\"]', 'null', 0.00, NULL, NULL, NULL, '2022-11-01', '2022-12-31', 'Government of Madhya Pradesh', 0.00, 'gautamitimmapur@triwits.com', '2022-11-08 11:02:27', 1, 'prajwitagadade@triwits.com', '2022-11-24 03:54:20', 1, '', '', '', '', NULL, NULL),
