lication process<br>', '100% tuition fee waiver', '[\"Bachelors\"]', '[\"Bachelors (All)\"]', '\"Undergraduates\"', NULL, NULL, 0.00, 0.00, 0.00, 0.00, '[\"\"]', '[\"\"]', '[\"\"]', NULL, '[\"\"]', NULL, '[\"\"]', 0, 0, '[\"\"]', 0.00, '', '[\"\"]', 'null', 0.00, NULL, NULL, NULL, '2022-11-01', '2022-12-31', 'Government of Madhya Pradesh', 0.00, 'gautamitimmapur@triwits.com', '2022-11-08 11:34:30', 1, 'gautamitimmapur@triwits.com', '2022-11-08 06:08:02', 1, '', '', '', '', NULL, NULL),
