(1914, '7nXtV25pG688JVNA8M', 'Competitions and Awards', 'UNESCO Sultan Qaboos Prize for Environmental Conservation ', 'https://forms.office.com/pages/responsepage.aspx?id=Uq5PHbM5-kuwswIpVrERlFtomJyhGNxAj5kDjhIGW4ZUQjdLRDE3M0YxRUlKRFc3VE1TTUs3TUtQMi4u', '', '', '', '', '', ' This prize is awarded every two years for outstanding contributions by individuals, groups of individuals, institutes or organizations in the field of the environment</br>\r\n<li> It consists of a diploma and a financial reward of US$100,000 and it is awarded during the World Science Forum</br>\r\n<li> Nominations for the Prize can only be made by UNESCO Members States in consultation with their National Commissions, and by Non-Governmental Organizations maintaining official partnerships with UNESCO, and active in relevant fields covered by the Prize</br>\r\n<li> Each Member State or NGO may only nominate one candidate</br>\r\n<li> Other interested NGOs, research institutions, universities and individuals, are not eligible to make nominations directly to UNESCO</br>\r\n<li> Therefore, such bodies and individuals are invited to propose their candidate to their country National Commission for UNESCO, which may or may not select it as its formal candidature</br></br>\r\n<b>Eligibility</b></br>\r\n<li> Self-nominations cannot be considered</br>\r\n<li> Candidatures should be prepared in English or French. They must include</br>\r\nA nomination form (available on this page when a call for nominations is open)</br>\r\nA recommendation letter from the Member State or NGO</br>\r\n<li> An endorsement letter from the National Commission of the country concerned</br>\r\n<li> Additional information, such as publications, articles, or scientific papers, may also be included in support of each nomination</br></br>', '', '<li> Hit the below âClick here & applyâ button</br>\r\n<li> Read the scholarship details carefully</br>\r\n<li> Fill out the form & attach the necessary documents</br>\r\n<li> submit</br></br>', '$100,000', '[\"All\"]', 'null', '\"All\"', NULL, NULL, 0.00, 0.00, 0.00, 0.00, 'null', 'null', 'null', NULL, 'null', NULL, 'null', 0, 0, 'null', 0.00, '', 'null', 'null', 0.00, 0, 0, 0, '2023-04-30', '2023-07-15', 'Ministry of AYUSH', 0.00, 'asham@triwits.com', '2023-05-26 16:40:20', 1, 'asham@triwits.com', '2023-05-26 11:12:14', 1, '', '', '', '', NULL, NULL),
(1915, 'LUf7PzKqovTHMvT8bV', 'Scholarship', 'Aikyashree Scholarship', 'http://wbmdfcscholarship.org/main/student_panel', '', '', '', '', '', 'Aikyashree Scholarship is a scholarship program offered by the West Bengal government to provide financial assistance to students from economically and socially disadvantaged backgrounds who wish to pursue higher education. The scholarship is targeted at students who have passed the West Bengal Board of Secondary Education (WBBSE) or West Bengal Council of Higher Secondary Education (WBCHSE) examination and wish to study in fields such as engineering, medicine, and basic sciences.<br><br>\r\n<b>Benefits:</b>\r\n<li>Helping poor and needy students in getting loans for their study.\r\n<li>Empowering weaker sections of the society\r\n<li>Helping and understanding the concept of being financially stable.\r\n<li>Provide training for several courses\r\n<li>Giving chance to the students who deserve the scholarship\r\n<li> Spreading awareness among the people about how important education is.<br><br>\r\n<b>Eligibility :</b>\r\n<li>Being a resident of West Bengal\r\n<li>Having passed the WBBSE or WBCHSE examination\r\n<li>Securing a minimum of 60% marks in the examination\r\n<li>Belonging to a specific category (such as Scheduled Caste, Scheduled Tribe, Other Backward Classes, or Economically Weaker Section)\r\n<li>Having a certain level of family income\r\n<li>Enrolling in a recognized college or university in India\r\n\r\n', '<li>Domicile certificate\r\n<li>Income certificate\r\n<li>Bank account details\r\n<li>Passport size photo of the applicant\r\n<li>Educational certificates.\r\n', '<li>Hit the below âClick here & applyâ button\r\n<li>Read the Scholarship details carefully \r\n<li>Fillout the form & attach necessary documents carefully\r\n<li>Submit\r\n', '11000', '[\"Class 10 \",\"Class 11\",\"Class 12\",\"Medical\",\"Engineering\",\"Masters\"]', 'null', '\"10th, I & II PUC,MBBS, BE, BTech, CA\"', NULL, NULL, 0.00, 0.00, 0.00